#include <string>
#include <iostream>
#include <sstream>
#include <vector>
#include <stdexcept>

std::string markdownToHTML(std::string markdown) {
    std::vector<std::string> tokens;
    std::istringstream iss(markdown);
    std::string token;
    while (iss >> token) {
        tokens.push_back(token);
    }
    std::stringstream ss;
    for (const auto& t : tokens) {
        if (t.empty()) {
            continue;
        }
        if (t[0] == '#') {
            ss << "<h" << t.substr(1) << "> ";
        } else if (t[0] == '*') {
            ss << "<em> ";
        } else if (t[0] == '_') {
            ss << "<ins> ";
        } else if (t[0] == '^') {
            ss << "<sup> ";
        } else if (t[0] == '~') {
            ss << "<del> ";
        } else {
            ss << t << " ";
        }
        for (size_t i = 1; i < t.size(); ++i) {
            ss << t[i];
        }
        ss << "</" << t[0] << ">";
    }
    std::string result = ss.str();
    return result;
}
```

