      description: 'Ban a user from the server',
      options: [
        {
          name: 'user',
          description: 'The user to ban',
          type: 'user',
          required: true,
        },
        {
          name: 'reason',
          description: 'The reason for the ban',
          type: 'string',
          required: false,
        },
      ],
    });
  }

  async execute(interaction: Message) {
