#include "Snake.h"

void initSnake(snake* s, int x, int y, int w, int h) {
    s->pos[HEAD].x = x;
    s->pos[HEAD].y = y;
    s->pos[HEAD].w = w;
    s->pos[HEAD].h = h;

    s->pos[BODY].x = x + w;
    s->pos[BODY].y = y;
    s->pos[BODY].w = w;
    s->pos[BODY].h = h;

    s->pos[TAIL].x = x + 2 * w;
    s->pos[TAIL].y = y;
    s->pos[TAIL].w = w;
    s->pos[TAIL].h = h;

    s->dir = UP;
    s->currPart = HEAD;
    s->state = ALIVE;
}

void updateSnake(snake* s, int speed) {
    switch (s->dir) {
    case UP:
        s->pos[s->currPart].y -= speed;
        break;
    case DOWN:
        s->pos[s->currPart].y += speed;
        break;
    case LEFT:
        s->pos[s->currPart].x -= speed;
        break;
    case RIGHT:
        s->pos[s->currPart].x += speed;
        break;
    }

    // Update the snake's body
    for (int i = HEAD; i < TAIL; i++) {
        s->pos[i].x = s->pos[i - 1].x;
        s->pos[i].y = s->pos[i - 1].y;
    }

    // Update the tail
    s->pos[TAIL].x = s->pos[BODY].x;
    s->pos[TAIL].y = s->pos[BODY].y;
}

void drawSnake(snake* s, SDL_Renderer* renderer) {
    SDL_RenderDrawRect(renderer, &s->pos[HEAD]);
    SDL_RenderDrawRect(renderer, &s->pos[BODY]);
    SDL_RenderDrawRect(renderer, &s->pos[TAIL]);
}

int checkCollision(snake* s, int x, int y) {
    for (int i = HEAD; i < TAIL; i++) {
        if (s->pos[i].x == x && s->pos[i].y == y) {
            return 1;
        }
    }
    return 0;
}

void updateSnakeState(snake* s, int x, int y) {
    if (checkCollision(s, x, y)) {
        s->state = DEAD;
    } else {
        s->state = ALIVE;
    }
}
