  for (let i = 0; i < arr.length; i++) {
    const num = arr[i];
    if (num > 1 && num % 2 !== 0 && num % 3 !== 0 && num % 5 !== 0 && num % 7 !== 0 && num % 11 !== 0 && num % 13 !== 0) {
      console.log(`Found a prime number: ${num}`);
