Here's the C++ function:

#include <string>
#include <iostream>
#include <boost/algorithm/string.hpp>

std::string convertMarkdownToHtml(const std::string& markdown) {
  std::string html;

  // Remove the first character of the input string (the double quote) to get the actual markdown text
  std::string markdownText = markdown.substr(1);

  // Split the markdown text into individual lines
  std::vector<std::string> lines;
  boost::split(lines, markdownText, boost::is_any_of("\n"));

  // Iterate through each line and convert it to html
  for (auto& line : lines) {
    if (line[0] == '*' && line[1] != ' ') {
      // The line is a list item, so convert it to a <li> tag
      html += "<li>" + line.substr(2) + "</li>";
    } else {
      // The line is a normal sentence, so convert it to a <p> tag
      html += "<p>" + line + "</p>";
    }
  }

  // Add a header tag for the header
  html = "<h1>" + html + "</h1>";

  return html;
}

Here's an example of using the function:

int main() {
  std::string markdown = "\"**My Header**\n* Item 1\n* Item 2\n* Item 3\"";
  std::string html = convertMarkdownToHtml(markdown);

  std::cout << html << std::endl;

  return 0;
}

