    var input_dir = Vector3(0, 0, 0)
    if Input.is_action_pressed("ui_right"):
        input_dir.x += 1
    if Input.is_action_pressed("ui_left"):
        input_dir.x -= 1
    if Input.is_action_pressed("ui_up"):
        input_dir.z += 1
    if Input.is_action_pressed("ui_down"):
        input_dir.z -= 1

    input_dir = input_dir.normalized()
    var movement = input_dir * movement_speed * delta
    var jump = Input.is_action_pressed("jump") * jump_speed * delta

    var gravity_pull = Vector3(0, 0, 0)
    if is_on_floor():
        gravity_pull.y = -gravity * delta

    var total_force = movement + jump + gravity_pull
    apply_central_force(total_force)

    if is_on_floor() and Input.is_action_pressed("jump"):
        jump_speed = 10.0
    else:
        jump_speed = 0.0

