#include <string>
#include <boost/algorithm/string.hpp>
#include <boost/optional.hpp>
#include <boost/regex.hpp>
#include <iostream>

using namespace std;
using namespace boost;

string convert_markdown_to_html(const string& input) {
    // Remove leading and trailing whitespace
    input = input.substr(1, input.size() - 2);

    // Split input into individual words and punctuation
    vector<string> words_and_punctuation;
    boost::split(words_and_punctuation, input, boost::is_any_of(" \t\n\r<>[]{}()|;:"));

    // Initialize an empty output string
    string output;

    // Iterate over each word and punctuation mark
    for (auto& word_and_punctuation : words_and_punctuation) {
        // Check if the word is a punctuation mark
        if (word_and_punctuation.size() == 1) {
            // Handle punctuation marks
            switch (word_and_punctuation[0]) {
                case '>':
                    output += "<";
                    break;
                case '<':
                    output += ">";
                    break;
                case ' ':
                    output += "&nbsp;";
                    break;
                case '\n':
                    output += "<br>";
                    break;
                case '\r':
                    output += "<br>";
                    break;
                case '|':
                    output += "&vertbar;";
                    break;
                case ';':
                    output += "&semicolon;";
                    break;
                case ':':
                    output += "&colon;";
                    break;
                case ',':
                    output += "&comma;";
                    break;
                case '.':
                    output += "&period;";
                    break;
                case '?':
                    output += "&questionmark;";
                    break;
                case '!':
                    output += "&exclamationmark;";
                    break;
                default:
                    output += word_and_punctuation[0];
            }
        } else {
            // Handle words
            output += word_and_punctuation;
        }
    }

    // Return the output string
    return output;
}
```

