        if self.current_stack.is_empty():
            while not self.waiting_stack.is_empty():
                self.current_stack.push(self.waiting_stack.pop())
        return self.current_stack.peek()

    def is_empty(self):
        return self.waiting_stack.is_empty() and self.current_stack.is_empty()

