#include <pugixml.hpp>

using namespace boost;

std::string convert_markdown_to_html(const std::string& input_markdown) {
    std::string output_html;

    pugi::xml_document doc;
    doc.load_string(input_markdown.c_str());

    for (pugi::xml_node node : doc.children()) {
        if (node.name() == "p") {
            output_html += "<p>" + node.text().get() + "</p>";
        } else if (node.name() == "h1") {
            output_html += "<h1>" + node.text().get() + "</h1>";
        } else if (node.name() == "h2") {
            output_html += "<h2>" + node.text().get() + "</h2>";
        } else if (node.name() == "h3") {
