        } else if (node.name() == "h4") {
            output_html += "<h4>" + node.text().get() + "</h4>";
        } else if (node.name() == "h5") {
            output_html += "<h5>" + node.text().get() + "</h5>";
        } else if (node.name() == "h6") {
            output_html += "<h6>" + node.text().get() + "</h6>";
        } else if (node.name() == "li") {
            output_html += "<li>" + node.text().get() + "</li>";
        } else if (node.name() == "ol") {
            output_html += "<ol>" + node.text().get() + "</ol>";
        } else if (node.name() == "ul") {
            output_html += "<ul>" + node.text().get() + "</ul>";
        } else if (node.name() == "a") {
            output_html += "<a href=\"" + node.attribute("href").value() + "\">" + node.text().get() + "</a>";
        } else if (node.name() == "img") {
            output_html += "<img src=\"" + node.attribute("src").value() + "\" alt=\"" + node.text().get() + "\" />";
        }
    }

    return output_html;
}
