#include <iostream>
#include <fstream>
#include <string>
#include <vector>

using namespace std;

int main() {
    string wordToSearch; // the word we're looking for
    int count = 0; // the number of times the word appears

    cout << "Enter the word you want to search for: ";
    cin >> wordToSearch;

    // open the file and read it line by line
    ifstream file("textfile.txt");
    string line;
    while (getline(file, line)) {
        // split the line into individual words
        vector<string> words = split(line, " ");

        // search for the wordToSearch in the vector of words
        for (string word : words) {
            if (word == wordToSearch) {
                count++;
            }
        }
    }

    // close the file and display the count
    file.close();
    cout << "The word " << wordToSearch << " appears " << count << " times in the file.";

    return 0;
}

// helper function to split a string into individual words
vector<string> split(string line, string delimiter) {
    vector<string> words;
    string word = "";
    for (char c : line) {
        if (c != delimiter[0]) {
            word += c;
        } else {
            words.push_back(word);
            word = "";
        }
    }
    words.push_back(word);

    return words;
}
```

