std::string html_format(const std::string& markdown_text) {
  std::string html_text;

  // Replace markdown links with html links
  std::regex link_regex("[(]?(http|ftp)://[^\s]*[^\)]?");
  std::smatch link_match;
  while (std::regex_search(markdown_text, link_match, link_regex)) {
    html_text += "<a href=\"" + link_match[0] + "\">" + link_match[0] + "</a>";
    markdown_text = std::regex_replace(markdown_text, link_regex, "");
  }

  // Replace markdown images with html images
  std::regex image_regex("![^\s]*\(([^\)]+)\)");
  std::smatch image_match;
  while (std::regex_search(markdown_text, image_match, image_regex)) {
    html_text += "<img src=\"" + image_match[1] + "\" alt=\"" + image_match[0] + "\">";
    markdown_text = std::regex_replace(markdown_text, image_regex, "");
  }

  // Handle bold and italic text
  std::regex bold_italic_regex("__(.*?)__");
  std::smatch bold_italic_match;
  while (std::regex_search(markdown_text, bold_italic_match, bold_italic_regex)) {
    html_text += "<b><i>" + bold_italic_match[1] + "</i></b>";
    markdown_text = std::regex_replace(markdown_text, bold_italic_regex, "");
  }

  // Handle bold text
  std::regex bold_regex("__(.*?)__");
  std::smatch bold_match;
  while (std::regex_search(markdown_text, bold_match, bold_regex)) {
    html_text += "<b>" + bold_match[1] + "</b>";
    markdown_text = std::regex_replace(markdown_text, bold_regex, "");
  }

  // Handle italic text
  std::regex italic_regex("_(.*?)_");
  std::smatch italic_match;
  while (std::regex_search(markdown_text, italic_match, italic_regex)) {
    html_text += "<i>" + italic_match[1] + "</i>";
    markdown_text = std::regex_replace(markdown_text, italic_regex, "");
  }

  // Handle code blocks
  std::regex code_block_regex("```(.*?)```");
  std::smatch code_block_match;
  while (std::regex_search(markdown_text, code_block_match, code_block_regex)) {
    html_text += "<pre><code>" + code_block_match[1] + "</code></pre>";
    markdown_text = std::regex_replace(markdown_text, code_block_regex, "");
  }

  // Handle list items
  std::regex list_item_regex("[-|+] (.*?)");
  std::smatch list_item_match;
  while (std::regex_search(markdown_text, list_item_match, list_item_regex)) {
    html_text += "<li>" + list_item_match[1] + "</li>";
    markdown_text = std::regex_replace(markdown_text, list_item_regex, "");
  }

  // Handle paragraphs
  std::regex paragraph_regex("([^\s]*\n)*[^\s]*");
  std::smatch paragraph_match;
  while (std::regex_search(markdown_text, paragraph_match, paragraph_regex)) {
    html_text += paragraph_match[0] + "<br>";
    markdown_text = std::regex_replace(markdown_text, paragraph_regex, "");
  }

  html_text += markdown_text;

  return html_text;
}
