```c
#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <allegro5/allegro.h>

// Define some constants for the game
#define SNAKE_SPEED 5 // Speed of the snake in pixels per second
#define SNAKE_SIZE 20 // Size of the snake in pixels
#define SNAKE_COLOR ALLEGRO_COLOR{0, 0, 0} // Color of the snake, set to black

// Create a struct for the snake's position and direction
struct Snake {
    int x, y; // Position
    int dx, dy; // Direction
};

// Function prototypes
void init_allegro(void);
void create_window(void);
void create_snake(struct Snake *snake);
void move_snake(struct Snake *snake);
void draw_snake(struct Snake *snake);

int main(void) {
    // Initialize Allegro
    init_allegro();

    // Create the window
    create_window();

    // Create the snake
    struct Snake snake;
    create_snake(&snake);

    // Main game loop
    while (true) {
        // Move the snake
        move_snake(&snake);

        // Draw the snake
        draw_snake(&snake);

        // Check for any Allegro events (like key presses)
        al_wait_for_event(NULL, NULL);

        // Clear the screen
        al_clear_to_color(ALLEGRO_COLOR{255, 255, 255});
    }

    return 0;
}

void init_allegro(void) {
    // Initialize Allegro
    al_init();

    // Set the color depth to 32-bit
    al_set_new_display_option(ALLEGRO_DEPTH, 32, ALLEGRO_SUGGEST);

    // Create the Allegro timer
    al_create_timer(1/60);

    // Start the timer
    al_start_timer(0);
}

void create_window(void) {
    // Create the window
    al_create_display(640, 480);

    // Set the title
    al_set_window_title("Snake Game");

    // Make the window visible
    al_show_display();
}

void create_snake(struct Snake *snake) {
    // Set the initial position of the snake
    snake->x = 300;
    snake->y = 240;

    // Set the initial direction of the snake
    snake->dx = SNAKE_SPEED;
    snake->dy = 0;
}

void move_snake(struct Snake *snake) {
    // Move the snake
    snake->x += snake->dx;
    snake->y += snake->dy;

    // Check for any collisions with the edges of the screen
    if (snake->x < 0 || snake->x > 640 || snake->y < 0 || snake->y > 480) {
        // Game over!
        exit(0);
    }
}

void draw_snake(struct Snake *snake) {
    // Draw the snake
    al_draw_filled_rectangle(snake->x, snake->y, snake->x + SNAKE_SIZE, snake->y + SNAKE_SIZE, SNAKE_COLOR);

    // Draw the snake's head
    al_draw_rectangle(snake->x, snake->y, snake->x + SNAKE_SIZE, snake->y + SNAKE_SIZE, SNAKE_COLOR);
}
```

