    cin >> wordToCount; // Read the word from the user

    ifstream file("textfile.txt"); // Open the text file

    if (file.is_open()) {
        string line; // A string to hold each line of the file

        while (getline(file, line)) {
            // Read each line of the file into the string 'line'

            int index = 0; // Initialize the index to 0

            while (index < line.length()) {
                // Check if the wordToCount is equal to the current character in the line

                if (line[index] == wordToCount[0]) {
                    // If the word starts with the current character, check the rest of the word

                    if (line.substr(index, wordToCount.length()) == wordToCount) {
                        count++; // Increment the count if the word matches
                    }
                }

                index++; // Increment the index to move to the next character
            }
        }

        file.close(); // Close the file
    }

    cout << "The word " << wordToCount << " occurs " << count << " times in the file." << endl;

    return 0;
}

