#include <string>
#include <boost/algorithm/string.hpp>
#include <boost/optional.hpp>
#include <boost/regex.hpp>

using namespace boost;

std::string markdown_to_html(const std::string &markdown_text) {
  // Remove leading and trailing whitespace from the input string
  markdown_text = remove_leading_trailing_whitespace(markdown_text);

  // Split the input string into individual words and punctuation
  vector<string> words_and_punctuation;
  split(words_and_punctuation, markdown_text, boost::is_any_of(" \t\n\r<>[]{}|\\^~`'\""));

  // Initialize an empty output string
  std::string html_text;

  // Iterate over each word and punctuation in the input string
  for (auto &word_and_punctuation : words_and_punctuation) {
    // Handle punctuation
    switch (word_and_punctuation[0]) {
    case ' ':
    case '\t':
    case '\n':
    case '\r':
    case '<':
    case '>':
    case '[':
    case ']':
    case '{':
    case '}':
    case '|':
    case '\\':
    case '^':
    case '~':
    case '`':
    case ' ':
    case '"':
    case '\'':
      html_text += word_and_punctuation;
      break;

    default:
      // Handle words
      if (word_and_punctuation.size() > 1) {
        // Handle links
        if (word_and_punctuation[0] == '!' && word_and_punctuation[1] != ' ') {
          html_text += "<a href=\"" + word_and_punctuation.substr(1) + "\">" + word_and_punctuation.substr(1) + "</a>";
          continue;
        }
        // Handle emphasis
        if (word_and_punctuation[0] == '*' && word_and_punctuation[1] != ' ') {
          html_text += "<em>" + word_and_punctuation.substr(1) + "</em>";
          continue;
        }
        // Handle strikethrough
        if (word_and_punctuation[0] == '~' && word_and_punctuation[1] != ' ') {
          html_text += "<s>" + word_and_punctuation.substr(1) + "</s>";
          continue;
        }
        // Handle code blocks
        if (word_and_punctuation[0] == '`' && word_and_punctuation[1] != ' ') {
          html_text += "<pre><code>" + word_and_punctuation.substr(1) + "</code></pre>";
          continue;
        }
        // Handle headings
        if (word_and_punctuation[0] == '#' && word_and_punctuation[1] != ' ') {
          html_text += "<h" + (word_and_punctuation[1] - '0') + ">" + word_and_punctuation.substr(1) + "</h" + (word_and_punctuation[1] - '0') + ">";
          continue;
        }
        // Handle paragraphs
        if (word_and_punctuation[0] == '|' && word_and_punctuation[1] != ' ') {
          html_text += "<p>" + word_and_punctuation.substr(1) + "</p>";
          continue;
        }
        // Handle lists
        if (word_and_punctuation[0] == '-' && word_and_punctuation[1] != ' ') {
          html_text += "<li>" + word_and_punctuation.substr(1) + "</li>";
          continue;
        }
        // Handle quotes
        if (word_and_punctuation[0] == '"' && word_and_punctuation[1] != ' ') {
          html_text += "<q>" + word_and_punctuation.substr(1) + "</q>";
          continue;
        }
        // Handle italic
        if (word_and_punctuation[0] == '_' && word_and_punctuation[1] != ' ') {
          html_text += "<i>" + word_and_punctuation.substr(1) + "</i>";
          continue;
        }
        // Handle bold
        if (word_and_punctuation[0] == '+' && word_and_punctuation[1] != ' ') {
          html_text += "<b>" + word_and_punctuation.substr(1) + "</b>";
          continue;
        }
      }
      html_text += word_and_punctuation;
    }
  }

  return html_text;
}
```

This function uses a combination of regular expressions and string manipulation to convert markdown formatted text into html formatted text. It handles various types of punctuation, words, and phrases to produce a valid html string.

