let container: any;

before(() => {
  // start a docker container
  docker = new Docker({ socketPath: '/var/run/docker.sock' });
  let image = 'my-app-image';
  let containerName = 'my-container';
  let inspect = async (containerName) => {
    let containerInspect = await docker.getContainer(containerName).inspect();
    return containerInspect;
  };
  let containerExists = async (containerName) => {
    let list = await docker.listContainers({ all: true });
    let exists = list.find(container => container.name === containerName);
    return !!exists;
  };
  if (!containerExists(containerName)) {
    await docker.run(image, { name: containerName });
  } else {
    container = await inspect(containerName);
  }
});

after(() => {
  // stop the docker container
  if (container) {
    await docker.getContainer(container.id).remove({ force: true });
  }
});

describe('My tests', () => {
  beforeEach(() => {
    // set up the test environment
    Cypress.config.baseUrl = `http://localhost:${container.port}`;
  });

  it('should have a working homepage', () => {
    cy.visit('/');
    cy.get('h1').should('have.text', 'Welcome to my app!');
    cy.get('button').should('have.text', 'Go to the dashboard');
    cy.get('img').should('have.attr', 'src', 'image.png');
  });

  afterEach(() => {
    // take a screenshot of the page
    Cypress.screenshot('after-test', { fullPage: true });
  });
});

