```cpp
#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string word = "specific word";
    int count = 0;
    string line;
    fstream file("file.txt");
    while (getline(file, line)) {
        int pos = line.find(word);
        while (pos != string::npos) {
            count++;
            pos = line.find(word, pos + 1);
        }
    }
    cout << "The number of occurrences of the word " << word << " is: " << count << endl;
