#include <iostream>
#include <string>
#include <boost/algorithm/string.hpp>
#include <boost/regex.hpp>

std::string markdown_to_html(const std::string& markdown_text) {
    // Define the regular expressions for markdown syntax
    boost::regex markdown_escaped_regex("^[^]*$");
    boost::regex heading_regex("^#(\\w+)$");
    boost::regex paragraph_regex("^[^]*$");
    boost::regex unordered_list_regex("^[^]*$");
    boost::regex ordered_list_regex("^[^]*$");
    boost::regex bold_regex("^[^]*$");
    boost::regex italic_regex("^[^]*$");
    boost::regex link_regex("^[^]*$");
    boost::regex image_regex("^[^]*$");

    // Define the replacement patterns for each markdown syntax
    std::string heading_replacement("<heading>");
    std::string paragraph_replacement("<paragraph>");
    std::string unordered_list_replacement("<unordered_list>");
    std::string ordered_list_replacement("<ordered_list>");
    std::string bold_replacement("<bold>");
    std::string italic_replacement("<italic>");
    std::string link_replacement("<link>");
    std::string image_replacement("<image>");

    // Create a string to store the HTML output
    std::string html_output;

    // Split the input markdown text into lines
    std::vector<std::string> lines;
    boost::split(lines, markdown_text, boost::is_any_of("\n"));

    // Process each line of markdown text
    for (const auto& line : lines) {
        // Check for escaped lines (lines starting with ^)
        if (boost::regex_match(line, markdown_escaped_regex)) {
            html_output += line;
        }

        // Check for headings
        else if (boost::regex_match(line, heading_regex)) {
            html_output += heading_replacement;
        }

        // Check for paragraphs
        else if (boost::regex_match(line, paragraph_regex)) {
            html_output += paragraph_replacement;
        }

        // Check for unordered lists
        else if (boost::regex_match(line, unordered_list_regex)) {
            html_output += unordered_list_replacement;
        }

        // Check for ordered lists
        else if (boost::regex_match(line, ordered_list_regex)) {
            html_output += ordered_list_replacement;
        }

        // Check for bold text
        else if (boost::regex_match(line, bold_regex)) {
            html_output += bold_replacement;
        }

        // Check for italicized text
        else if (boost::regex_match(line, italic_regex)) {
            html_output += italic_replacement;
        }

        // Check for links
        else if (boost::regex_match(line, link_regex)) {
            html_output += link_replacement;
        }

        // Check for images
        else if (boost::regex_match(line, image_regex)) {
            html_output += image_replacement;
        }
    }

    return html_output;
}

int main() {
    std::string markdown_text = "Here is a heading\n"
                              "Here is a paragraph\n"
                              "Here is an unordered list:\n"
                              "- Item 1\n"
                              "- Item 2\n"
                              "Here is a link: [link](https://example.com)\n"
                              "Here is an image: ![image](image.jpg)";

    std::string html_text = markdown_to_html(markdown_text);

    std::cout << "Markdown text: " << markdown_text << std::endl;
    std::cout << "HTML text: " << html_text << std::endl;

    return 0;
}
```

