```c++
#include <iostream>
#include <string>
#include <sstream>
using namespace std;
string markdown_to_html(string& markdown) {
    stringstream ss;
    ss << markdown;
    string html = ss.str();
    html.replace(html.find("##"), 2, "<h2>");
    html.replace(html.find("###"), 3, "<h3>");
    html.replace(html.find("####"), 4, "<h4>");
    html.replace(html.find("#####"), 5, "<h5>");
    html.replace(html.find("######"), 6, "<h6>");
    html.replace(html.find("$$"), 2, "<span class='math'>");
    html.replace(html.find("$$"), 2, "</span>");
    html.replace(html.find("``"), 2, "<code>");
    html.replace(html.find("``"), 2, "</code>");
    html.replace(html.find(">"), 1, "&gt;");
    html.replace(html.find(" " + string(" "), 1, "&nbsp;");
    html.replace(html.find("\n"), 1, "<br>");
    return html;
}
int main() {
    string markdown = "## Heading\n\n*Italic*\n**Bold**\n***Bold and Italic***\n\n$$Equation$$\n\n``Code``\n\n> Blockquote\n\nThis is a blockquote.\n";
    string html = markdown_to_html(markdown);
    cout << html << endl;
    return 0;
}
```
