            // Underline or bold, convert to HTML
            html += "<b>";
        } else if (iss.peek() == '**' || iss.peek() == '***') {
            // Double underline or bold, convert to HTML
            html += "<b>";
        } else if (iss.peek() == '=' || iss.peek() == '==' || iss.peek() == '!') {
            // Code block, convert to HTML
            html += "<pre>";
        } else {
            // Handle special characters
            html += "<span class=\"" + std::string(1, iss.peek()) + "\">" + ss.str() + "</span>";
