```cpp
#include <string>
#include <vector>
#include <sstream>
#include <boost/algorithm/string.hpp>

std::string markdownToHtml(const std::string& markdown) {
    // Split the markdown string into a vector of strings, separated by newline characters
    std::vector<std::string> lines;
    boost::split(lines, markdown, boost::is_any_of("\n"));

    // Initialize an empty string to store the html output
    std::string html;

    // Iterate over each line of markdown
    for (const auto& line : lines) {
        // Check if the line is a header
        if (line[0] == '#') {
            // Extract the header level and text
            int level = line[1] - '0';
            std::string text = line.substr(2);

            // Generate the html header tag
            html += "<h" + std::to_string(level) + ">" + text + "</h" + std::to_string(level) + ">\n";
        } else if (line[0] == '*') {
            // Extract the list item text
            std::string text = line.substr(1);

            // Generate the html list item tag
            html += "<li>" + text + "</li>\n";
        } else {
            // Assume the line is a paragraph of text
            html += "<p>" + line + "</p>\n";
        }
    }

    return html;
}
```
