```c
#include <allegro5/allegro.h>
#include <allegro5/allegro_image.h>

// Define the snake's movement speed
#define SNAKE_SPEED 2

// Define the snake's image
ALLEGRO_BITMAP *snake_image = NULL;

// Define the snake's x and y coordinates
int snake_x = 0;
int snake_y = 0;

// Define the snake's direction (up, down, left, or right)
int snake_direction = 0;

// Define the snake's length
int snake_length = 10;

// Define the snake's body parts
ALLEGRO_BITMAP *snake_body[10] = {NULL};

// Function to initialize the snake
void init_snake() {
    // Load the snake image
    snake_image = al_load_bitmap("snake.png");

    // Create the snake's body parts
    for (int i = 0; i < snake_length; i++) {
        snake_body[i] = al_create_bitmap(10, 10);
        al_draw_bitmap(snake_image, 0, 0, snake_body[i]);
    }
}

// Function to update the snake's position
void update_snake() {
    // Update the snake's x and y coordinates based on the direction
    switch (snake_direction) {
        case 0: // Up
            snake_y -= SNAKE_SPEED;
            break;
        case 1: // Down
            snake_y += SNAKE_SPEED;
            break;
        case 2: // Left
            snake_x -= SNAKE_SPEED;
            break;
        case 3: // Right
            snake_x += SNAKE_SPEED;
            break;
    }

    // Move the snake's body parts
    for (int i = snake_length - 1; i > 0; i--) {
        al_draw_bitmap(snake_body[i - 1], snake_x + i * 10, snake_y, NULL);
    }

    // Draw the snake's head
    al_draw_bitmap(snake_image, snake_x, snake_y, NULL);
}

// Function to handle key presses
void handle_keys() {
    if (al_key_down(&al_get_keyboard_state(), ALLEGRO_KEY_UP)) {
        snake_direction = 0;
    } else if (al_key_down(&al_get_keyboard_state(), ALLEGRO_KEY_DOWN)) {
        snake_direction = 1;
    } else if (al_key_down(&al_get_keyboard_state(), ALLEGRO_KEY_LEFT)) {
        snake_direction = 2;
    } else if (al_key_down(&al_get_keyboard_state(), ALLEGRO_KEY_RIGHT)) {
        snake_direction = 3;
    }
}

int main() {
    // Initialize Allegro
    al_init();

    // Create a window
    al_create_window("Snake Game", 640, 480, NULL);

    // Initialize the snake
    init_snake();

    // Main game loop
    while (true) {
        // Handle key presses
        handle_keys();

        // Update the snake's position
        update_snake();

        // Display the current frame
        al_flip_display();

        // Wait for a short period of time
        al_rest(1.0 / 60.0);
    }

    return 0;
}
```
