extends KinematicBody

export var speed = 10.0
export var turn_speed = 0.1

var direction = Vector3(0, 0, 0)

func _process(delta):
    var input_map = get_input_map()
    direction = input_map.direction

    if input_map.has_action("ui_up"):
        direction.y += 1
    if input_map.has_action("ui_down"):
        direction.y -= 1
    if input_map.has_action("ui_left"):
        direction.x += 1
    if input_map.has_action("ui_right"):
        direction.x -= 1

    direction = direction.normalized()
    move_and_slide(direction * speed, Vector3(0, 0, 0), true)

    var turn_input = input_map.turn
    if turn_input != 0:
        turn(turn_input * turn_speed)

