    def push(self, element):
        self.stack.append(element)

    def pop(self):
        if len(self.stack) > 0:
            return self.stack.pop()
        else:
            raise IndexError("The stack is empty.")

    def top(self):
        if len(self.stack) > 0:
            return self.stack[-1]
        else:
            raise IndexError("The stack is empty.")

