```c++
#include <iostream>
#include <string>
#include <sstream>
using namespace std;
string markdown_to_html(string& markdown) {
    stringstream html;
    string line;
    bool in_list = false;
    bool in_code_block = false;
    for (string::iterator it = markdown.begin(); it != markdown.end(); ++it) {
        if (!in_list && !in_code_block && (*it == '\n' || *it == '\t' || *it == ' ')) {
            html << "<br>";
        } else if (*it == '\n' && in_list) {
            html << "</ul>";
            in_list = false;
        } else if (*it == '\n' && !in_list && in_code_block) {
            html << "</code>";
            in_code_block = false;
        } else if (*it == '\n') {
            html << "<p>";
            in_list = true;
        } else if (*it == '\t' || *it == ' ') {
            if (in_code_block) {
                html << *it;
            } else {
                html << "&nbsp;";
            }
        } else if (*it == '`') {
            in_code_block = !in_code_block;
        } else {
            html << *it;
        }
    }
    if (in_list) {
        html << "</ul>";
    }
    if (in_code_block) {
        html << "</code>";
    }
    return html.str();
}
