#include <string>
#include <iostream>

std::string convertMarkdownToHtml(const std::string& markdown) {
    std::string html;
    // Convert markdown headers to html headers
    if (markdown.find("#") != std::string::npos) {
        std::string headerLevel = markdown.substr(0, 1);
        std::string headerText = markdown.substr(1];
        html += "<h" + headerLevel + ">" + headerText + "</h" + headerLevel + ">";
    }
    // Convert markdown lists to html lists
    if (markdown.find("*") != std::string::npos) {
        std::string listItem = markdown.substr(1];
        html += "<li>" + listItem + "</li>";
    }
    return html;
}

int main() {
