#include <allegro5/allegro_font.h>
#include <allegro5/allegro_ttf.h>
#include <allegro5/allegro_primitives.h>

#define SCREEN_W 800
#define SCREEN_H 600

ALLEGRO_DISPLAY *display;
ALLEGRO_EVENT_QUEUE *event_queue;
ALLEGRO_TIMER *timer;
ALLEGRO_BITMAP *snake_img, *apple_img, *background_img;

ALLEGRO_COLOR snake_color = {0, 0, 0};
ALLEGRO_COLOR apple_color = {255, 0, 0};
ALLEGRO_COLOR background_color = {255, 255, 255};

int snake_x = 400, snake_y = 300;
int apple_x = 500, apple_y = 400;
int background_x = 0, background_y = 0;

void draw_background() {
    al_draw_bitmap(background_img, background_x, background_y, 0);
}

void draw_snake() {
    al_draw_bitmap_snapped(snake_img, snake_x, snake_y, 0, 0, 0, 0, 0, 0);
}

void draw_apple() {
    al_draw_bitmap_snapped(apple_img, apple_x, apple_y, 0, 0, 0, 0, 0, 0);
}

void move_snake() {
    int new_snake_x, new_snake_y;

    if (al_get_key_state(ALLEGRO_KEY_UP) == ALLEGRO_KEY_DOWN) {
        new_snake_y--;
    }
    if (al_get_key_state(ALLEGRO_KEY_DOWN) == ALLEGRO_KEY_UP) {
        new_snake_y++;
    }
    if (al_get_key_state(ALLEGRO_KEY_LEFT) == ALLEGRO_KEY_RIGHT) {
        new_snake_x--;
    }
    if (al_get_key_state(ALLEGRO_KEY_RIGHT) == ALLEGRO_KEY_LEFT) {
        new_snake_x++;
    }
    snake_x = new_snake_x;
    snake_y = new_snake_y;

    if (snake_x < 0) snake_x = 0;
    if (snake_x > SCREEN_W - 1) snake_x = SCREEN_W - 1;
    if (snake_y < 0) snake_y = 0;
    if (snake_y > SCREEN_H - 1) snake_y = SCREEN_H - 1;

    al_clear_to_color(background_color);
    draw_background();
    draw_snake();
    draw_apple();
    al_flip_display();
