#define SNAKE_IMAGE "snake.gif"
#define SNAKE_WIDTH 10
#define SNAKE_HEIGHT 10
#define SNAKE_STEP 10

void drawSnake(int x, int y, int direction)
{
    BITMAP *snakeBmp = LoadBitmap(SNAKE_IMAGE);
    int snakeX = x - SNAKE_WIDTH / 2;
    int snakeY = y - SNAKE_HEIGHT / 2;
    int snakeStep = SNAKE_STEP * direction;
    int snakeStepX = snakeStep % SNAKE_WIDTH;
    int snakeStepY = snakeStep / SNAKE_WIDTH;

    for (int i = 0; i < SNAKE_WIDTH; i++)
    {
        for (int j = 0; j < SNAKE_HEIGHT; j++)
        {
            if (snakeBmp->data[i + j * SNAKE_WIDTH] != 0)
            {
                SetPixel(x + i, y + j, snakeBmp->data[i + j * SNAKE_WIDTH]);
            }
        }
    }
    for (int i = 0; i < SNAKE_WIDTH; i++)
    {
        for (int j = 0; j < SNAKE_HEIGHT; j++)
        {
            if (snakeBmp->data[i + j * SNAKE_WIDTH] != 0)
            {
                SetPixel(x + i + snakeStepX, y + j + snakeStepY, snakeBmp->data[i + j * SNAKE_WIDTH]);
            }
        }
    }
    FreeBitmap(snakeBmp);
}

void main()
{
    int x = 100, y = 100, direction = 1;
    int keypress = 0;
    while (1)
    {
        drawSnake(x, y, direction);
        delay(100);
        keypress = GetKey();
        if (keypress == 0)
