            break;
        case 0x7A: // up arrow
            direction = -1;
            break;
        case 0x7C: // down arrow
            direction = 1;
            break;
        }
        x += direction * SNAKE_STEP;
        y += direction * SNAKE_STEP / 2;
    }
}
