```c
#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>
#include <stdio.h>
#include <math.h>

#define SCREEN_WIDTH 800
#define SCREEN_HEIGHT 600

#define SNAKE_SPEED 5

struct Snake {
    int x, y;
    int direction;
    SDL_Texture *texture;
};

struct Level {
    SDL_Texture *background;
    int width, height;
};

struct Game {
    SDL_Window *window;
    SDL_Renderer *renderer;
    SDL_Texture *snake_texture;
    SDL_Texture *background_texture;
    Snake *snake;
    Level *level;
};

void init_game(Game *game) {
    SDL_Init(SDL_INIT_VIDEO);
    game->window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    game->renderer = SDL_CreateRenderer(game->window, -1, SDL_RENDERER_ACCELERATED);
    game->snake_texture = SDL_CreateTextureFromSurface(game->renderer, SDL_LoadBMP("snake.bmp"));
    game->background_texture = SDL_CreateTextureFromSurface(game->renderer, SDL_LoadBMP("background.bmp"));
    game->snake = malloc(sizeof(Snake));
    game->level = malloc(sizeof(Level));
    game->level->width = SCREEN_WIDTH;
    game->level->height = SCREEN_HEIGHT;
    game->snake->x = SCREEN_WIDTH / 2;
    game->snake->y = SCREEN_HEIGHT / 2;
    game->snake->direction = 0;
    game->snake->texture = game->snake_texture;
}

void update_game(Game *game) {
    SDL_Event event;
    while (SDL_PollEvent(&event)) {
        if (event.type == SDL_QUIT) {
            exit(0);
        }
    }
    if (game->snake->direction == 0) {
        game->snake->x += SNAKE_SPEED;
    } else if (game->snake->direction == 1) {
        game->snake->y += SNAKE_SPEED;
    } else if (game->snake->direction == 2) {
        game->snake->x -= SNAKE_SPEED;
    } else if (game->snake->direction == 3) {
        game->snake->y -= SNAKE_SPEED;
    }
    SDL_RenderClear(game->renderer);
    SDL_RenderCopy(game->renderer, game->background_texture, NULL, NULL);
    SDL_RenderCopy(game->renderer, game->snake->texture, NULL, &game->snake->x);
    SDL_RenderPresent(game->renderer);
    SDL_Delay(100);
}

int main() {
    Game game;
    init_game(&game);
    while (1) {
        update_game(&game);
    }
    return 0;
}
