#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>
#include <stdio.h>
#include <string>
#define SCREEN_WIDTH 800
#define SCREEN_HEIGHT 600
#define SNAKE_WIDTH 20
#define SNAKE_HEIGHT 20
#define SNAKE_SPEED 10
#define SNAKE_DIR_LEFT 0
#define SNAKE_DIR_RIGHT 1
#define SNAKE_DIR_UP 2
#define SNAKE_DIR_DOWN 3
typedef struct {
    int x;
    int y;
} SnakePart;
int main(int argc, char* argv[]) {
    SDL_Init(SDL_INIT_VIDEO);
    IMG_Init(IMG_INIT_PNG);
    SDL_Window* window = SDL_CreateWindow("Snake", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
    SDL_SetRenderDrawColor(renderer, 0, 0, 0);
    SDL_RenderClear(renderer);
    SnakePart snake[100];
    int snake_length = 5;
    int snake_dir = SNAKE_DIR_DOWN;
    int score = 0;
    int food_x = rand() % (SCREEN_WIDTH - SNAKE_WIDTH);
    int food_y = rand() % (SCREEN_HEIGHT - SNAKE_HEIGHT);
    for (int i = 0; i < snake_length; i++) {
        snake[i].x = -SNAKE_WIDTH;
        snake[i].y = i * SNAKE_HEIGHT;
    }
    while (1) {
        SDL_Event event;
        while (SDL_PollEvent(&event)) {
            if (event.type == SDL_KEYDOWN) {
                switch (event.key.keysym.sym) {
                    case SDLK_LEFT:
                        if (snake_dir != SNAKE_DIR_RIGHT) {
                            snake_dir = SNAKE_DIR_LEFT;
                        }
                        break;
                    case SDLK_RIGHT:
                        if (snake_dir != SNAKE_DIR_LEFT) {
                            snake_dir = SNAKE_DIR_RIGHT;
                        }
                        break;
                    case SDLK_UP:
                        if (snake_dir != SNAKE_DIR_DOWN) {
                            snake_dir = SNAKE_DIR_UP;
                        }
                        break;
                    case SDLK_DOWN:
                        if (snake_dir != SNAKE_DIR_UP) {
                            snake_dir = SNAKE_DIR_DOWN;
                        }
                        break;
                    case SDLK_SPACE:
                        score += 10;
                        food_x = rand() % (SCREEN_WIDTH - SNAKE_WIDTH);
                        food_y = rand() % (SCREEN_HEIGHT - SNAKE_HEIGHT);
                        break;
                    case SDLK_q:
                        exit(0);
                        break;
                    default:
                        break;
                }
            }
        }
        for (int i = snake_length - 1; i > 0; i--) {
            snake[i].x = snake[i - 1].x;
            snake[i].y = snake[i - 1].y;
        }
        switch (snake_dir) {
            case SNAKE_DIR_LEFT:
                snake[0].x -= SNAKE_SPEED;
                break;
            case SNAKE_DIR_RIGHT:
                snake[0].x += SNAKE_SPEED;
                break;
            case SNAKE_DIR_UP:
                snake[0].y -= SNAKE_SPEED;
                break;
            case SNAKE_DIR_DOWN:
                snake[0].y += SNAKE_SPEED;
                break;
            default:
                break;
        }
        if (snake[0].x < 0) {
            snake[0].x = SCREEN_WIDTH;
        } else if (snake[0].x > SCREEN_WIDTH) {
            snake[0].x = 0;
        }
        if (snake[0].y < 0) {
            snake[0].y = SCREEN_HEIGHT;
        } else if (snake[0].y > SCREEN_HEIGHT) {
            snake[0].y = 0;
        }
        if (snake[0].x == food_x && snake[0].y == food_y) {
            score += 10;
            snake_length++;
            food_x = rand() % (SCREEN_WIDTH - SNAKE_WIDTH);
            food_y = rand() % (SCREEN_HEIGHT - SNAKE_HEIGHT);
        }
        for (int i = 1; i < snake_length; i++) {
            if (snake[0].x == snake[i].x && snake[0].y == snake[i].y) {
                snake_length = i;
                score -= 10;
            }
        }
        SDL_SetRenderDrawColor(renderer, 0, 0, 0);
        SDL_RenderClear(renderer);
        for (int i = 0; i < snake_length; i++) {
            SDL_Rect snake_rect = {snake[i].x, snake[i].y, SNAKE_WIDTH, SNAKE_HEIGHT};
            SDL_Texture* snake_texture = IMG_LoadTexture(renderer, "snake.png");
            SDL_SetTextureColorMod(snake_texture, 255, 255, 255);
            SDL_SetTextureBlendMode(snake_texture, SDL_BLENDMODE_BLEND);
            SDL_RenderCopy(renderer, snake_texture, NULL, &snake_rect);
        }
        SDL_Rect food_rect = {food_x, food_y, SNAKE_WIDTH, SNAKE_HEIGHT};
        SDL_Texture* food_texture = IMG_LoadTexture(renderer, "food.png");
        SDL_SetTextureColorMod(food_texture, 255, 255, 255);
        SDL_SetTextureBlendMode(food_texture, SDL_BLENDMODE_BLEND);
        SDL_RenderCopy(renderer, food_texture, NULL, &food_rect);
        SDL_SetTextureColorMod(snake_texture, 255, 255, 255);
        SDL_SetTextureColorMod(food_texture, 255, 255, 255);
        SDL_RenderPresent(renderer);
        SDL_Delay(10);
    }
    SDL_DestroyTexture(snake_texture);
    SDL_DestroyTexture(food_texture);
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    IMG_Quit();
    SDL_Quit();
    return 0;
}
```
In this code snippet, we use the SDL library to create a window and a renderer, load two images (snake.png and food.png), and create a SnakePart struct to store the snake's position. We then set up a timer to control the snake's movement, and use a while loop to continuously update the snake's position and draw it on the screen.
