class Queue:
    def __init__(self):
        self.stack1 = []
        self.stack2 = []

    def enqueue(self, item):
        self.stack1.append(item)

    def dequeue(self):
        if not self.stack1 and not self.stack2:
            raise IndexError("Queue is empty")

        if not self.stack2:
