#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>

#include <stdio.h>

// Game constants
#define SCREEN_WIDTH 640
#define SCREEN_HEIGHT 480
#define NUM_FRAMES 8 // number of animation frames

// Load and store the snake's animation frames
SDL_Texture *snake_textures[NUM_FRAMES];
SDL_Surface *snake_surface;
SDL_Rect snake_clip;

// Game variables
SDL_Window *window;
SDL_Renderer *renderer;
int snake_x, snake_y; // snake's position
int snake_dir; // snake's direction (0: up, 1: right, 2: down, 3: left)
