// Function to initialize the game
void init_game() {
    // Initialize SDL and image loading
    SDL_Init(SDL_INIT_EVERYTHING);
    IMG_Init(IMG_INIT_PNG);

    // Create the game window
    window = SDL_CreateWindow("Snake", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    if (!window) {
        printf("Error: Unable to create window\n");
        return;
    }

    // Create a renderer for rendering graphics
    renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
    if (!renderer) {
        printf("Error: Unable to create renderer\n");
        return;
    }

    // Load the snake's animation frames
    snake_surface = IMG_Load("snake.png");
    if (!snake_surface) {
        printf("Error: Unable to load snake image\n");
        return;
    }

    // Convert the surface to a texture
    snake_textures[0] = SDL_CreateTextureFromSurface(renderer, snake_surface);
    if (!snake_textures[0]) {
        printf("Error: Unable to create texture\n");
        return;
    }

    // Set the snake's starting position and direction
    snake_x = SCREEN_WIDTH / 2;
    snake_y = SCREEN_HEIGHT / 2;
    snake_dir = 0; // start moving up

    // Initialize the score
    score = 0;
}

// Function to update the snake's position and animation
void update_snake() {
    // Update the snake's position based on its direction
    switch (snake_dir) {
        case 0:
            snake_y -= 10; // move up
            break;
        case 1:
            snake_x += 10; // move right
            break;
        case 2:
            snake_y += 10; // move down
            break;
        case 3:
            snake_x -= 10; // move left
            break;
    }

    // Wrap the snake around the screen if it goes out of bounds
    if (snake_x < 0) {
        snake_x = SCREEN_WIDTH;
    } else if (snake_x > SCREEN_WIDTH) {
        snake_x = 0;
    } else if (snake_y < 0) {
        snake_y = SCREEN_HEIGHT;
    } else if (snake_y > SCREEN_HEIGHT) {
        snake_y = 0;
    }

    // Update the snake's animation frame
    snake_clip.x = (snake_dir % NUM_FRAMES) * snake_surface->w;
    snake_clip.y = (snake_dir / NUM_FRAMES) * snake_surface->h;
    snake_clip.w = snake_surface->w / NUM_FRAMES;
    snake_clip.h = snake_surface->h / NUM_FRAMES;
}

// Function to render the game graphics
void render_game() {
    // Clear the screen
    SDL_SetRenderDrawColor(renderer, 0, 0, 0, 0);
    SDL_RenderClear(renderer);

    // Draw the snake
    SDL_RenderCopy(renderer, snake_textures[snake_dir], &snake_clip, NULL);

    // Draw the game's background (optional)
    // SDL_RenderCopy(renderer, background_texture, NULL, NULL);

    // Display the score
    SDL_TextInput *score_text = SDL_CreateText(renderer, "Score: %d", 0, 0, 0);
    SDL_RenderCopy(renderer, score_text, NULL, NULL);

    // Update the screen
    SDL_RenderPresent(renderer);
}

// Function to handle user input
void handle_input() {
    // Get user input (e.g., keyboard or gamepad)
    SDL_Event event;
    while (SDL_PollEvent(&event)) {
        if (event.type == SDL_KEYDOWN) {
            // Change the snake's direction based on the user's input
            switch (event.key.keysym.sym) {
                case SDLK_UP:
                    snake_dir = 0;
                    break;
                case SDLK_RIGHT:
                    snake_dir = 1;
                    break;
                case SDLK_DOWN:
                    snake_dir = 2;
                    break;
                case SDLK_LEFT:
                    snake_dir = 3;
                    break;
            }
        }
    }
}

// Main game loop
int main() {
    init_game();

    while (true) {
        handle_input();
        update_snake();
        render_game();

        // Delay the game loop to create a smooth animation
        SDL_Delay(100); // 100 milliseconds
    }

    // Clean up resources
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();
    return 0;
}
```
