  for (let num of arr) {
    if (isPrimeNumber(num)) {
      console.log(num);
      return;
    }
  }
}

function isPrimeNumber(num) {
  if (num < 2) return false;
  for (let i = 2; i <= Math.sqrt(num); i++) {
