const { Client, GatewayIntentBits } = require("discord.js");

const client = new Client({
  intents: [GatewayIntentBits.Guilds, GatewayIntentBits.GuildMembers, GatewayIntentBits.GuildBans],
});

client.on("ready", () => {
  console.log("Bot is ready to assist you!");
});

client.on("interactionCreate", async (interaction) => {
  if (interaction.commandName === "ban") {
    await interaction.guild.ban(interaction.user, {
      reason: "You're not allowed to use this command.",
    });
    await interaction.reply("You've been banned from the guild!");
  }
});

client.login(process.env.TOKEN).catch((e) => {
  console.error(e);
