#include <string>
#include <boost/algorithm/string.hpp>
#include <boost/regex.hpp>

std::string markdownToHTML(const std::string& markdown) {
    // Remove leading and trailing whitespace
    markdown = boost::trim(markdown);
    // Split the input into individual lines
    std::vector<std::string> lines;
    boost::split(lines, markdown, boost::is_any_of("\n"));
    // Initialize the output string
    std::string html;
    // Iterate over each line and process it
    for (auto line : lines) {
        // Remove leading and trailing whitespace
        line = boost::trim(line);
        // Check if the line starts with a hash (#) and is a header
        if (line[0] == '#' && line[1] != ' ') {
            // Convert the line to HTML
            html += "<h" + (line[1] - '0') + ">" + boost::regex_replace(line, boost::regex("\\#"), "<span class=\"header\">\\#</span>") + "</h" + (line[1] - '0') + ">";
        } else {
            // Convert the line to HTML
            html += "<p>" + boost::regex_replace(line, boost::regex("\\n"), "<br />") + "</p>";
        }
    }
    return html;
}
```

