#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>
#include <stdio.h>

#define SCREEN_WIDTH 640
#define SCREEN_HEIGHT 480
#define SNAKE_WIDTH 32
#define SNAKE_HEIGHT 32

struct Snake {
    SDL_Texture *image;
    int x, y, width, height, direction;
};

void init_snake(struct Snake *snake, char *image_path) {
    snake->image = SDL_LoadTexture(image_path, NULL);
    snake->x = 0;
    snake->y = 0;
    snake->width = SNAKE_WIDTH;
    snake->height = SNAKE_HEIGHT;
    snake->direction = 0;  // 0 = up, 1 = right, 2 = down, 3 = left
}

void handle_input(struct Snake *snake, SDL_Event *event) {
    switch (event->type) {
    case SDL_KEYDOWN:
        switch (event->key.keysym.sym) {
        case SDLK_UP:
            snake->direction = 0; break;
        case SDLK_RIGHT:
            snake->direction = 1; break;
        case SDLK_DOWN:
            snake->direction = 2; break;
        case SDLK_LEFT:
            snake->direction = 3; break;
        }
    }
}

void update_snake_position(struct Snake *snake) {
    switch (snake->direction) {
    case 0: snake->y--; break;
    case 1: snake->x++; break;
    case 2: snake->y++; break;
    case 3: snake->x--; break;
    }
    snake->x += snake->width / 2;
    snake->y += snake->height / 2;
}

void draw_snake(struct Snake *snake, SDL_Renderer *renderer) {
    SDL_RenderCopy(renderer, snake->image, NULL, &snake->rect);
}

int main(int argc, char *argv[]) {
    SDL_Init(SDL_INIT_VIDEO);
    SDL_Window *window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    SDL_Renderer *renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
    struct Snake snake;
    init_snake(&snake, "snake.png");
    SDL_Event event;
    while (1) {
        handle_input(&snake, &event);
        update_snake_position(&snake);
        draw_snake(&snake, renderer);
        SDL_RenderPresent(renderer);
    }
    return 0;
}
```

