    def wrapper(*args, **kwargs):
        start_time = time.time()
        result = func(*args, **kwargs)
        end_time = time.time()
        print(f"Function '{func.__name__}' took {end_time - start_time} milliseconds.")
        return result
    return wrapper

@timeit
def calculate_sum(numbers):
