    } catch (error) {
      console.error(`Error banning ${member.user.tag}: ${error}`);
      interaction.reply({ content: 'Error banning the user.', ephemeral: true });
    }
  }
});

client.on('error', error => {
  console.error('An error occurred: ', error);
});
```

